using System;
using System.Collections.Generic;
using System.Linq;

using Xenial.Framework.Badges.Win;

namespace Xenial.Framework.Badges.Win
{
    internal static class ControllerTypeList
    {
        public static Type[] ControllerTypes { get; } = new[]
        {
            typeof(AdornerWindowsFormsCustomizeNavigationController)
        };
    }
}

namespace DevExpress.ExpressApp
{
    /// <summary>
    /// Class ControllerTypeListExtension.
    /// </summary>
    /// <autogeneratedoc />
    public static partial class ControllerTypeListExtension
    {
        /// <summary>
        /// Uses the xenial badges windows forms controller types.
        /// </summary>
        /// <param name="types">The types.</param>
        /// <returns>IEnumerable&lt;Type&gt;.</returns>
        /// <autogeneratedoc />
        public static IEnumerable<Type> UseXenialBadgesWindowsFormsControllerTypes(this IEnumerable<Type> types)
            => types.Concat(ControllerTypeList.ControllerTypes);
    }
}
