using System;

namespace Xenial.Framework.Utils.Slugger
{
    /// <summary>
    /// Class DefaultSlugifier.
    /// </summary>
    /// <autogeneratedoc />
    public static class DefaultSlugifier
    {
        private static SlugifierConfig defaultConfig = new SlugifierConfig();
        private static Slugifier @default = new Slugifier(defaultConfig);

        /// <summary>
        /// Gets or sets the default configuration.
        /// </summary>
        /// <value>The default configuration.</value>
        /// <exception cref="ArgumentNullException">defaultConfig</exception>
        /// <autogeneratedoc />
        public static SlugifierConfig DefaultConfig
        {
            get => defaultConfig;
            set
            {
                defaultConfig = value ?? throw new ArgumentNullException(nameof(defaultConfig));
                Default = new Slugifier(value);
            }
        }

        /// <summary>
        /// Gets or sets the default <see cref="Slugifier"/>.
        /// </summary>
        /// <value>The default.</value>
        /// <autogeneratedoc />
        public static Slugifier Default
        {
            get => @default;
            set => @default = value ?? throw new ArgumentNullException(nameof(Default));
        }

        /// <summary>
        /// Slugifies the specified string.
        /// </summary>
        /// <param name="string">The string.</param>
        /// <returns>System.String.</returns>
        /// <autogeneratedoc />
        public static string Slugify(this string @string)
            => Default.GenerateSlug(@string);
    }
}

